package week.d2.Assignments;

public class NegativeToPositiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int neg = -40;
      int pos;
      if (neg < 0) {
		System.out.println(neg + " is a negative number.");
		pos = neg*(-1);
		System.out.println(neg + " is copnverted to a positive number " + pos + ".");
	}
	}

}
